import { TracingProcessor } from '@openai/agents-core/src/tracing/processor';
import { getGlobalTraceProvider } from '@openai/agents-core/src/tracing/provider';

export {
  getCurrentSpan,
  getCurrentTrace,
  getOrCreateTrace,
  resetCurrentSpan,
  setCurrentSpan,
  withTrace,
} from '@openai/agents-core/src/tracing/context';
export * from '@openai/agents-core/src/tracing/createSpans';
export {
  BatchTraceProcessor,
  TracingExporter,
  TracingProcessor,
  ConsoleSpanExporter,
} from '@openai/agents-core/src/tracing/processor';
export { NoopSpan, Span } from '@openai/agents-core/src/tracing/spans';
export { NoopTrace, Trace } from '@openai/agents-core/src/tracing/traces';
export { generateGroupId, generateSpanId, generateTraceId } from '@openai/agents-core/src/tracing/utils';

/**
 * Add a processor to the list of processors. Each processor will receive all traces/spans.
 *
 * @param processor - The processor to add.
 */
export function addTraceProcessor(processor: TracingProcessor): void {
  getGlobalTraceProvider().registerProcessor(processor);
}

/**
 * Set the list of processors. This will replace any existing processors.
 *
 * @param processors - The list of processors to set.
 */
export function setTraceProcessors(processors: TracingProcessor[]): void {
  getGlobalTraceProvider().setProcessors(processors);
}

/**
 * Set the disabled state of the tracing provider.
 *
 * @param disabled - Whether to disable tracing.
 */
export function setTracingDisabled(disabled: boolean): void {
  getGlobalTraceProvider().setDisabled(disabled);
}

/**
 * Start the trace export loop.
 */
export function startTraceExportLoop(): void {
  getGlobalTraceProvider().startExportLoop();
}
